import XCTest
@testable import youda

class ReceiptTest: XCTestCase {
  private func receiptService() -> ReceiptService {
    guard
      let receiptData = Data(base64Encoded: .receipt),
      let receiptService = try? ReceiptService(deviceID: nil, asn1: receiptData) else {
      fatalError()
    }
    return receiptService
  }

  func testReceipt() {
    let receipt = receiptService().receipt

    XCTAssertEqual(receipt.bundleId, .bundleId)
    XCTAssertEqual(receipt.purchases.count, 13)
    XCTAssertEqual(receipt.purchases.first?.productIdentifier, .productIdentifier)
  }

  // TODO: When swift package manager start support resources, uncomment add AppleRootInc certificate
//    func testCertificateVerification() {
//        XCTAssertNoThrow(try receiptService().verifyCertificate())
//    }
}

private extension String {
  static let bundleId = "sk.jantimar.News.sk"

  static let productIdentifier = "sk_pro_version"

